CREATE TABLE CLUB if not exists(
	"ID" INTEGER  IDENTITY PRIMARY KEY, 
	"LIBELLE" VARCHAR(100),
	INDEX(LIBELLE)
)

CREATE TABLE PARTICIPANT (
	"ID" INTEGER  IDENTITY PRIMARY KEY, 
	"NOM" VARCHAR(100),
	"PRENOM" VARCHAR(100),
	"ID_CLUB" INTEGER,
	FOREIGN KEY(ID_CLUB) REFERENCES CLUB (ID)
)

