CREATE TABLE CATEGORIE (
	"ID" INTEGER  IDENTITY PRIMARY KEY, 
	"LIBELLE" VARCHAR(100)
)

CREATE TABLE PARTICIPANT_CATEGORIE (
	"ID" INTEGER  IDENTITY PRIMARY KEY, 
	"ID_PARTICIPANT" INTEGER,
	"ID_CATEGORIE" INTEGER,
	FOREIGN KEY(ID_PARTICIPANT) REFERENCES PARTICIPANT (ID),
	FOREIGN KEY(ID_CATEGORIE) REFERENCES CATEGORIE (ID)
)

CREATE TABLE PARAMETRE (
	"ID" INTEGER  IDENTITY PRIMARY KEY, 
	"LIBELLE" VARCHAR(200),
	"DATE_C" VARCHAR(10),
	"LIEUX" VARCHAR(200),
	"DESCRIPTION" VARCHAR(800)
)

CREATE TABLE COMBAT (
	"ID" INTEGER  IDENTITY PRIMARY KEY, 
	"ID_CATEGORIE" INTEGER,	  
	"ID_PARTICIPANT_1" INTEGER,	 
	"ID_PARTICIPANT_2" INTEGER,
	"RESULTAT" VARCHAR(200),
	FOREIGN KEY(ID_PARTICIPANT_1) REFERENCES PARTICIPANT (ID),
	FOREIGN KEY(ID_PARTICIPANT_2) REFERENCES PARTICIPANT (ID),
	FOREIGN KEY(ID_CATEGORIE) REFERENCES CATEGORIE (ID)
)

CREATE TABLE TIRAGE (
	"ID" INTEGER  IDENTITY PRIMARY KEY, 
	"ID_PART_CAT" INTEGER,
	"NUMERO_POULE" INTEGER,
	"POSITION_POULE" INTEGER,
	FOREIGN KEY(ID_PART_CAT) REFERENCES PARTICIPANT_CATEGORIE (ID)
)